import Vuex from 'vuex'
let store= new Vuex.Store({ // объект Vuex.Store
state: {
     products:[]
},// состояние в нем хранятся переменные массивы объекты
mutations:{},// мутации- при помощи из мы меняем данные состояния state, они синхронны
// т.е выполняются  по очереди  
actions:{},// чтобы не было очереди, они асинхронны можно вызвать 5 действий 
getters:{
    PRODUCTS(state){
        return state.products;
    }
} // короткий путь до получения информации о данных в сейте 
});
export default store;
